CREATE TABLE if not exists resources (	id int8 NOT NULL GENERATED ALWAYS AS IDENTITY,	address varchar NOT NULL,	"name" varchar NULL,	CONSTRAINT resources_pk PRIMARY KEY (id));CREATE TABLE if not exists users (	id int8 NOT NULL GENERATED ALWAYS AS IDENTITY,	chat varchar NOT NULL,	active bool NULL DEFAULT false,	CONSTRAINT users_pk PRIMARY KEY (id));CREATE TABLE if not exists journal (	id int8 NOT NULL GENERATED ALWAYS AS IDENTITY,	resource int8 NULL,	created_at timestamp NULL,	CONSTRAINT journal_pk PRIMARY KEY (id));ALTER TABLE journal ADD CONSTRAINT journal_fk FOREIGN KEY (resource) REFERENCES resources(id) ON DELETE CASCADE ON UPDATE CASCADE;